INSERT INTO "public"."qiao_cms_content" ("id", "createby", "createbyid", "creationdate", "updateby", "updatebyid", "updatedate", "compile", "content", "menuid", "sequence", "subtitle", "title", "version_") VALUES ('117057', NULL, NULL, '2018-07-03 09:57:09.369', NULL, NULL, '2018-07-03 09:57:09.357', NULL, '乔（qiao，古属角音）得姓于春秋时期鲁桓公十八年（公元前694年）梁国渭桥，是轩辕黄帝的嫡裔。轩辕黄帝其先为诸侯长，听从神农的指挥，后接任神农的天下。生两子少昊和昌意，昌意生子颛顼，高阳氏颛顼生子帝喾，高辛氏帝喾生尧姓伊祈；帝喾的元妃姜  原生子后稷，名子叫弃，是文武王的先祖；帝喾又生子契是司徒汤的先祖；帝喾又生子皋陶。皋陶生伯益，伯益的长子封地于梁也就是梁伯爵，赐姓嬴，建都少梁，就是现在陕西西安韩城县。梁伯劳民伤财治国无道至鲁桓公十八年被秦所灭，梁伯次子为渭桥邑大夫，他的后代以地为姓，因而姓桥。到了汉朝有桥茂、桥蕊相继被封为诸侯。后周文帝为相，命桥去木，义取高远。后又有乔玄，后汉东吴有乔公，寄居金陵，后代散居周围各地，后代中至唐朝有乔恭很有名望。 元末住居凤阳南小岗集乔恭后代与逃荒要饭来此落户的明太祖朱元璋父子相处为邻，死后葬在小岗集西南，明太祖父母死后也葬在这里，到明太祖修皇陵时将该坟一同修入陵内，每年皇帝祭陵时乔氏都要陪祭。生有二子，长子被封为邻户世袭挥签衙门，在皇陵白玉街生四子叫大四门，是卫籍也就是皇陵卫；次子被封为邻户，岁岁进皇城内玉石井中取水，封为世袭隆秀英杰，生四子叫小四门为民籍。我始迁祖大公海和胞弟二公江、三公河就是小四门（据多方查询，我们始迁祖还有个弟弟叫乔汉，正好是四兄弟）。后来迁到长淮卫北钓鱼台傍居住，当时属凤阳县北八都一图二甲关城村。此后因家贫，年年陪皇帝祭陵也陪不起，二公江、三公河一个到河南归德府永城县居住，一个到河南归德府零陵县居住，我始迁祖大公海于明永乐年间（公元1403--1425）到灵璧县南乡固镇桥南添堰寺，以教书为生，定居发脉，子孙蕃衍至今。 始迁祖安葬于前庄家东、解河北岸、固--何--怀公路西侧，二世祖龙，三世祖四公垒与之同陵，已修墓立碑。 现子孙迁居至周围6县50多村，散居者遍布全国各地，也有到国外定居的。', '26', '1', '乔氏源流考', '乔氏源流考', '1');
